      formattedHistory = historyRecords.reverse().map(record => {
        let roleToUse = 'unknown';
        if (typeof record.role === 'string') {
            const dbRole = record.role.toLowerCase();
            if (dbRole === 'user') {
                roleToUse = 'user';
            } else if (dbRole === 'assistant' || dbRole === 'ai' || dbRole === 'model') {
                roleToUse = 'assistant';
            } else {
                console.warn(`Format Recent History: Unknown role '${dbRole}' found, mapping to 'user'. Content: ${(record.content || "").substring(0,50)}`);
