            }
        } else {
             console.warn(`Format Recent History: Missing or invalid role for record. Defaulting to 'user'. Record:`, record);
             roleToUse = 'user';
        }
        const content = (typeof record.content === 'string') ? record.content : '';

        return {
          role: roleToUse, 
          content: content
        };
      });
    }
}

