  original_content_for_db: inputJson.original_content_for_db,
  role_for_final_insert: inputJson.role_for_db || inputJson['role-for-db'] || 'unknown',
  embedding_vector_string_for_db: vectorStringForDb // Outputting the string
};

if (!outputData.session_id_for_insert || !outputData.original_content_for_db || !outputData.role_for_final_insert) {
    console.error("Format Vector for PG: CRITICAL - Missing required fields. Data:", outputData);
    return null; 
}

return outputData;

// --- Intelligent Chunking Script v3 (with Debugging) ---

// This line now gets the session_id from the node that created the project in the database.
const BATCH_SESSION_ID = $('Webhook2').item.json.body.projectName;
const MAX_CHUNK_SIZE_CHARS = 6000; // Safety limit (~1500 tokens)
const documentText = $json.data;
const finalItems = [];

// Split the document by our manual break first.
const primaryChunks = documentText.split('---CHUNK_BREAK---');

for (const primaryChunk of primaryChunks) {
    let currentChunk = primaryChunk.trim();
    if (!currentChunk) continue;

    // If a chunk is already small enough, process it.
    if (currentChunk.length <= MAX_CHUNK_SIZE_CHARS) {
        // --- DEBUGGING ---
        console.log(`[OK] Chunk size: ${currentChunk.length}`);
        finalItems.push({
            json: {
                chunk_text_for_embedding: currentChunk,
                session_id_for_db: BATCH_SESSION_ID
            }
        });
        continue;
    }

    // If a chunk is TOO LARGE, we must split it further.
    // --- DEBUGGING ---
    console.log(`[LARGE CHUNK DETECTED] Splitting chunk of size: ${currentChunk.length}`);
    let tempChunk = "";
    const sentences = currentChunk.split(/(?<=[.?!])\s+/); // Split by sentences

    for (const sentence of sentences) {
        if ((tempChunk.length + sentence.length + 1) > MAX_CHUNK_SIZE_CHARS) {
            // --- DEBUGGING ---
            console.log(`[SUB-CHUNK CREATED] Size: ${tempChunk.length}`);
            finalItems.push({
                json: {
                    chunk_text_for_embedding: tempChunk,
                    session_id_for_db: BATCH_SESSION_ID
                }
            });
            tempChunk = sentence;
        } else {
            tempChunk += (tempChunk ? " " : "") + sentence;
        }
    }
    // Add the last remaining part
    if (tempChunk) {
        // --- DEBUGGING ---
        console.log(`[FINAL SUB-CHUNK] Size: ${tempChunk.length}`);
        finalItems.push({
            json: {
                chunk_text_for_embedding: tempChunk,
                session_id_for_db: BATCH_SESSION_ID
            }
        });
    }
}

return finalItems;

