// Get the data from the previous node.
const data = $input.item.json;

// --- FIX: Generate a NEW rag_session_id ---
// We will create a unique ID for the RAG memory store.
const ragSessionId = 'rag_' + Math.random().toString(36).substring(2, 9);

// Get the project name from the webhook's body property.
const projectName = data.body.projectName;

// Generate a unique session ID for the project itself.
const sessionId = 'proj_' + Date.now() + '_' + Math.random().toString(36).substring(2, 9);

// Set default values for the columns that actually exist.
const leadContact = "";
const status = "New";
const description = "";
const notes = "";

// This is the chat session ID, which can be empty for a new project.
const chat_session_id = ""; 

// --- Assemble the final parameters for the SQL query ---
const sql_params = [
  projectName,
  sessionId,
  status,
  leadContact,
  description,
  notes,
  ragSessionId // Use our newly generated ID
];

// Return the parameters for the next node.
return {
  json: {
    sql_params: sql_params
  }
};

// Generate a unique session ID for the new thread
const new_thread_id = `session_${Math.random().toString(36).slice(2, 11)}`;
return { new_thread_id: new_thread_id };
