const MAX_CHUNK_SIZE_CHARS = 6000; // Safety limit (~1500 tokens)
const documentText = $json.data;
const finalItems = [];

// Split the document by our manual break first.
const primaryChunks = documentText.split('---CHUNK_BREAK---');

for (const primaryChunk of primaryChunks) {
    let currentChunk = primaryChunk.trim();
    if (!currentChunk) continue;

    // If a chunk is already small enough, process it.
    if (currentChunk.length <= MAX_CHUNK_SIZE_CHARS) {
        // --- DEBUGGING ---
        console.log(`[OK] Chunk size: ${currentChunk.length}`);
        finalItems.push({
            json: {
                chunk_text_for_embedding: currentChunk,
                session_id_for_db: BATCH_SESSION_ID
            }
        });
        continue;
    }

    // If a chunk is TOO LARGE, we must split it further.
    // --- DEBUGGING ---
    console.log(`[LARGE CHUNK DETECTED] Splitting chunk of size: ${currentChunk.length}`);
    let tempChunk = "";
    const sentences = currentChunk.split(/(?<=[.?!])\s+/); // Split by sentences

    for (const sentence of sentences) {
        if ((tempChunk.length + sentence.length + 1) > MAX_CHUNK_SIZE_CHARS) {
            // --- DEBUGGING ---
            console.log(`[SUB-CHUNK CREATED] Size: ${tempChunk.length}`);
            finalItems.push({
                json: {
                    chunk_text_for_embedding: tempChunk,
                    session_id_for_db: BATCH_SESSION_ID
                }
            });
            tempChunk = sentence;
        } else {
            tempChunk += (tempChunk ? " " : "") + sentence;
        }
    }
