// This node takes the JSON string from the AI, extracts the facts,
// and returns each fact as a separate item to be embedded.

// Get the summary_text which contains a JSON string of facts.
const summaryJsonString = $input.item.json.summary_text;

// Parse the string to get the actual JSON object.
const summaryObject = JSON.parse(summaryJsonString);

// Get the array of facts from the object.
const factsArray = summaryObject.facts;

// If there are no facts, return an empty array to stop the workflow.
if (factsArray.length === 0) {
  return [];
}

// Return each fact as a separate item, with the correct key.
return factsArray.map(fact => {
  return {
    json: {
      "chunk_text_for_embedding": fact
    }
  }
});

// This node finds the highest 'id' from the incoming messages.
const items = $input.all();
let maxId = 0;

// If there are no items, the watermark is 0.
if (items.length === 0) {
  return [{ json: { new_high_water_mark: maxId } }];
}

// Find the highest id in the list of messages.
for (const item of items) {
  if (item.json.id > maxId) {
    maxId = item.json.id;
  }
}

// Return the single highest value.
return [{ json: { new_high_water_mark: maxId } }];

// This is the corrected final assembly. It pulls data from all necessary
// previous nodes to create the complete payload for the database.

// 1. Get the new embedding.
const embedding = $json.data[0].embedding;

// 2. Get the summary text.
const summaryData = $('Set: Extracted Summary').first().json;
const summaryText = summaryData.summary_text;

// 3. Get the project ID.
const projectData = $('Get Project ID').first().json;
const projectId = projectData.project_id;

// 4. Get the RAG session ID to scope long-term memory writes.
const triggerData = $('Trigger: Receive Session ID').first().json;
const sessionId = triggerData.body.rag_session_id;

// 5. Get the new high-water mark we just calculated.
const watermarkData = $('Calculate New High-Water Mark').first().json;
const newHighWaterMark = watermarkData.new_high_water_mark;

// 6. Build the final, complete payload with EVERYTHING needed.
const finalPayload = {
  project_id: projectId,
  session_id: sessionId,
  original_content: summaryText,
  role: 'summary',
  embedding: embedding,
  new_high_water_mark: newHighWaterMark
};

return finalPayload;

