// AI Router Input Processor
var body = $json.body || $json || {};
var selectedModel = body.selected_model || body.model || 'gpt-4o';
var messages = body.messages || [];

// Determine if this is a Claude model - be very specific
var modelLower = selectedModel.toLowerCase();
var isClaudeModel = modelLower.includes('claude-') || modelLower.startsWith('claude');

// For GPT models, ensure they are NOT Claude
if (modelLower.includes('gpt') || modelLower.includes('openai')) {
  isClaudeModel = false;
}

// Debug logging
console.log('=== AI Router Debug ===');
console.log('Selected Model:', selectedModel);
console.log('Model Lower:', modelLower);
console.log('Is Claude Model:', isClaudeModel);
console.log('Expected Path:', isClaudeModel ? 'Claude' : 'OpenAI');

return {
  selectedModel: selectedModel,
  messages: messages,
  isClaudeModel: isClaudeModel,
