    selectedModel: selectedModel,
    isClaudeModel: isClaudeModel,
    modelLower: modelLower,
    expectedPath: isClaudeModel ? 'Claude' : 'OpenAI'
  }
};

// Claude Preprocessor v6.2 - Handles both HTTPS URLs and data URLs
console.log('=== CLAUDE PREPROCESSOR v6.2 START ===');
console.log('Input $json:', JSON.stringify($json, null, 2));

var originalPayload = $json.originalPayload || {};
var messages = $json.messages || [];
var selectedModel = $json.selectedModel;

console.log('Extracted values:', {
  originalPayload: originalPayload,
  messagesCount: messages.length,
  selectedModel: selectedModel
});

// Map frontend model names to ACTUAL Claude API model identifiers (2025)
var claudeModelMap = {
  'claude-4-sonnet': 'claude-sonnet-4-20250514',
  'claude-4.1-opus': 'claude-opus-4-1-20250805',
  'claude-4-opus': 'claude-opus-4-20250514',
  'claude-3.7-sonnet': 'claude-3-7-sonnet-20250219',
  'claude-3-7-sonnet': 'claude-3-7-sonnet-20250219',
  'claude-3.5-haiku': 'claude-3-5-haiku-20241022',
  'claude-3-haiku': 'claude-3-haiku-20240307',
  'claude-haiku-3.5': 'claude-3-5-haiku-20241022'
};

// Get the correct Claude API model identifier
var claudeApiModel = claudeModelMap[selectedModel] || 'claude-sonnet-4-20250514';

console.log('=== Model Mapping ===');
console.log('Frontend Model:', selectedModel);
console.log('Mapped Claude Model:', claudeApiModel);
console.log('Message count:', messages.length);

// Convert OpenAI format to Claude format
var systemMessage = '';
var filteredMessages = [];

console.log('=== Starting Message Processing ===');
console.log('Raw messages:', JSON.stringify(messages, null, 2));

for (var i = 0; i < messages.length; i++) {
  console.log('=== Processing Message', i, '===');
  console.log('Message role:', messages[i].role);
  console.log('Message content type:', typeof messages[i].content);
  console.log('Is content array:', Array.isArray(messages[i].content));

  if (messages[i].role === 'system') {
    systemMessage = messages[i].content || '';
    console.log('Found system message, length:', systemMessage.length);
  } else {
    var messageContent = messages[i].content;
    console.log('Processing non-system message with content:', typeof messageContent);

    // Handle image content conversion from OpenAI to Anthropic format
    if (Array.isArray(messageContent)) {
      console.log('Found multipart content with', messageContent.length, 'items');
      var convertedContent = [];
      for (var j = 0; j < messageContent.length; j++) {
        var contentItem = messageContent[j];
        console.log('Processing content item', j, 'type:', contentItem.type);

        if (contentItem.type === 'image_url') {
          console.log('=== IMAGE PROCESSING START ===');
          console.log('Image URL structure:', JSON.stringify(contentItem, null, 2));

          // Extract image URL from OpenAI format
          var imageUrl = contentItem.image_url.url;
          var base64Data = '';
          var mediaType = 'image/jpeg'; // default

          console.log('Image URL type:', imageUrl.startsWith('data:') ? 'data URL' : 'HTTPS URL');

          // Handle different image URL formats
          if (imageUrl.startsWith('data:')) {
            // Extract base64 data from data URI
            console.log('Processing data URL...');
            var base64Start = imageUrl.indexOf(',') + 1;
            base64Data = imageUrl.substring(base64Start);

            // Extract media type
            var matches = imageUrl.match(/^data:([^;]+);base64,(.+)$/);
            if (matches && matches.length === 3) {
              mediaType = matches[1];
              base64Data = matches[2];
              console.log('Successfully extracted base64 data:', {
                mediaType: mediaType,
                dataLength: base64Data.length
              });
            } else {
              // Fallback parsing for malformed data URLs
              console.log('Using fallback parsing for data URL');
              if (imageUrl.includes('image/png')) mediaType = 'image/png';
              else if (imageUrl.includes('image/jpeg') || imageUrl.includes('image/jpg')) mediaType = 'image/jpeg';
              else if (imageUrl.includes('image/webp')) mediaType = 'image/webp';
              else if (imageUrl.includes('image/gif')) mediaType = 'image/gif';
              console.log('Fallback extraction successful:', {
                mediaType: mediaType,
                dataLength: base64Data.length
              });
            }

            // Clean and validate base64 data
            if (!base64Data || base64Data.length === 0) {
              console.error('=== ERROR: Empty base64 data ===');
              continue;
            }

            // Clean base64 data
            base64Data = base64Data.replace(/\s+/g, '').replace(/\n/g, '').replace(/\r/g, '');

            // Ensure it's valid base64 characters only
            base64Data = base64Data.replace(/[^A-Za-z0-9+/=]/g, '');

            // Pad if necessary
            if (base64Data.length % 4 !== 0) {
              var padding = 4 - (base64Data.length % 4);
              if (padding < 4) {
                base64Data += '='.repeat(padding);
              }
            }

            // Final validation
            try {
              var testData = base64Data.substring(0, Math.min(100, base64Data.length));
              if (testData.length >= 4) {
                atob(testData.substring(0, 4));
              }
              console.log('Base64 validation passed');
            } catch (e) {
              console.error('=== ERROR: Invalid base64 data ===');
              console.error('Error:', e.message);
              continue;
            }

            // Ensure media type is supported
            var supportedTypes = ['image/jpeg', 'image/png', 'image/webp', 'image/gif'];
            if (!supportedTypes.includes(mediaType)) {
              console.log('Unsupported media type, defaulting to image/jpeg:', mediaType);
              mediaType = 'image/jpeg';
            }

            console.log('=== IMAGE CONVERSION SUCCESS ===');
            console.log('Final image data:', {
              mediaType: mediaType,
              base64Length: base64Data.length,
              base64Preview: base64Data.substring(0, 20) + '...'
            });

            // Create Claude format for base64
            convertedContent.push({
              type: 'image',
              source: {
                type: 'base64',
                media_type: mediaType,
                data: base64Data
              }
            });

          } else if (imageUrl.startsWith('http')) {
            // For HTTPS URLs, pass directly to Claude (Claude supports URL format)
            console.log('Processing HTTPS URL:', imageUrl.substring(0, 100) + '...');

            // Determine media type from URL extension
            if (imageUrl.toLowerCase().includes('.png')) {
              mediaType = 'image/png';
            } else if (imageUrl.toLowerCase().includes('.jpg') || imageUrl.toLowerCase().includes('.jpeg')) {
              mediaType = 'image/jpeg';
            } else if (imageUrl.toLowerCase().includes('.webp')) {
              mediaType = 'image/webp';
            } else if (imageUrl.toLowerCase().includes('.gif')) {
              mediaType = 'image/gif';
            } else {
              mediaType = 'image/jpeg'; // default fallback
            }

            console.log('HTTPS URL will be passed directly to Claude:', {
              url: imageUrl.substring(0, 50) + '...',
              mediaType: mediaType
            });

            // Create Claude format for URL (not base64)
            convertedContent.push({
              type: 'image',
              source: {
                type: 'url',
                url: imageUrl
              }
            });

            console.log('=== IMAGE URL CONVERSION SUCCESS ===');

          } else {
            console.error('=== UNSUPPORTED IMAGE FORMAT ===');
            console.error('Only data: and https: URLs are supported. Received:', imageUrl.substring(0, 100));
            continue; // Skip this image
          }

        } else {
          // Keep text content as-is
          console.log('Keeping text content item:', contentItem.type || 'text');
          convertedContent.push(contentItem);
        }
      }
      messageContent = convertedContent;
      console.log('Converted content array length:', convertedContent.length);
    } else {
      console.log('Content is not array, keeping as text:', typeof messageContent);
    }

    filteredMessages.push({
      role: messages[i].role,
      content: messageContent
    });

    console.log('Processed message ' + i + ':', {
      role: messages[i].role,
      contentType: Array.isArray(messageContent) ? 'multipart' : 'text',
      contentLength: Array.isArray(messageContent) ? messageContent.length : messageContent.length
    });
  }
}

// Build Claude payload
var claudePayload = {
  model: claudeApiModel,
  max_tokens: originalPayload.max_tokens || 4000,
  messages: filteredMessages
};

if (systemMessage) {
  claudePayload.system = systemMessage;
}

// Final validation
console.log('=== FINAL PAYLOAD VALIDATION ===');
console.log('Model:', claudePayload.model);
console.log('Messages count:', claudePayload.messages ? claudePayload.messages.length : 'undefined');
console.log('System message present:', !!claudePayload.system);

if (!claudePayload.model || !claudePayload.messages || claudePayload.messages.length === 0) {
  console.error('=== CRITICAL ERROR: Invalid Claude payload ===');
  console.error('Full payload:', JSON.stringify(claudePayload, null, 2));
