        }
        if (content.source.type === 'url' && (!content.source.url || content.source.url.length === 0)) {
          console.error('Empty image URL in message', k, 'content', l);
          throw new Error('Invalid image: empty URL');
        }
        if (!content.source.media_type && content.source.type === 'base64') {
          content.source.media_type = 'image/jpeg';
        }
        console.log('Image validation passed for message', k, 'content', l, 'type:', content.source.type);
      }
    }
  }
}

console.log('=== PREPROCESSOR v6.2 SUCCESS ===');
console.log('Final Claude Payload:', JSON.stringify(claudePayload, null, 2));

